/// <reference path="Buffer.d.ts" />
declare module "msgpack" {
    module msgpack {
        /**
         * @brief 以 json 格式编码变量
         * @param data 要编码的变量
         * @return Class_Buffer
         */
        export function encode(data: any): Class_Buffer;
        /**
         * @brief 以 json 方式解码字符串为一个变量
         * @param data 要解码的字符串
         * @return 返回解码的变量
         */
        export function decode(data: Class_Buffer): any;
    } /** end of `module json` */
    export = msgpack
}